import { HttpErrorResponse, HttpStatusCode } from '@angular/common/http';
import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { Usuario } from 'src/app/Interfaces/usuario';
import { LoginService } from 'src/app/Services/login.service';
import Swal from 'sweetalert2';


@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {


  user = {} as Usuario;

  constructor(private router:Router, private loginServ: LoginService) { }

  ngOnInit(): void {
  }

  login() {
    if(this.validateParams()) {
			this.loginServ.postLogin(this.user.usuario, this.user.password).subscribe({
			  next: (response : Usuario) => {
          this.user = response;
          this.loginServ.setToken(this.user.id_usuario.toString(), this.user.usuario, 
                this.user.nombre_rol);

                Swal.fire({
                  title: 'Bienvenido',
                  icon: 'warning',
                  confirmButtonText: "Ok",
                });
          //      this.router.navigate(['/']);
				},
			  error: (err: HttpErrorResponse) => {
          if(err.status == HttpStatusCode.NotFound) {
            Swal.fire({
              title: 'Usuario y/o contraseña incorrecta',
              icon: 'warning',
              confirmButtonText: "Ok",
            });
          }
          if(err.status == HttpStatusCode.InternalServerError) {
            Swal.fire({
              title: 'Error en el Servicio',
              icon: 'error',
              confirmButtonText: "Ok",
          });
          }
			  },
			})
    } else {
      Swal.fire({
        title: 'Ingrese nombre de usuario y contraseña',
        icon: 'warning',
        confirmButtonText: "Ok",
      });
    }
  }

  validateParams() : boolean {
    if(this.user.usuario != null && this.user.usuario != "" &&
        this.user.password != null && this.user.password != "") {
          return true;
    }
    return false;
  }


}
