import { Component } from '@angular/core';
import { FormBuilder, FormGroup } from '@angular/forms';
import { Router } from '@angular/router';
import { Rol } from 'src/app/Interfaces/rol';
import { Usuario } from 'src/app/Interfaces/usuario';
import { UsuarioService } from 'src/app/Services/Usuario/Usuario/usuario.service';

@Component({
  selector: 'app-user',
  templateUrl: './user.component.html',
  styleUrls: ['./user.component.css']
})
export class UserComponent {

  formularioGroup: FormGroup;
  usuario: Usuario;
  roles: Rol[];

  private subscription = new Subscription();
  constructor(
    private userService: UsuarioService,
    private rolService: RolService,
    private router: Router,
    private formBuilder: FormBuilder
  ) { }

  ngOnInit(): void {

    this.formularioGroup= this.formBuilder.group({
      nombre: ['', [Validators.minLength(3), Validators.maxLength(100), Validators.required]],
      apellido: ['', [Validators.minLength(3), Validators.maxLength(80), Validators.required]],
      costoAproximado: [],
      contacto: ['', [Validators.email,Validators.required]],
      marcaSeleccionadaId: ['', Validators.required],
      modeloSeleccionadoId: ['', Validators.required]
    })

    this.presupuesto = new Presupuesto();

    this.subscription.add(
      this.marcaService.traerMarcas().subscribe({
        next: (listado: Marca[]) => {
          this.marcas = listado;
        },
        error: () => {
          alert('error al obtener marcas');
        },
      })
    );

    this.formularioGroup.get("marcaSeleccionadaId")?.valueChanges.subscribe((x: string) => {
      console.log('marca value changed')
      console.log(x)
      console.log(this.formularioGroup.get("marcaSeleccionadaId")?.value)


      this.subscription.add(
      this.modeloService.getModelos(x).subscribe({

        next: (respuesta: Modelo[]) => {
          this.modelos = respuesta;
        },
        error: () => {
          alert('error al comunicarse con la API');
          },
        }),
        );

      })
  }

  guardar() {
    if (this.formularioGroup.valid) {
      this.subscription.add(
        this.presService.agregar(this.presupuesto).subscribe({
          next: () => {
            this.irAListado();
          },
          error: () => {
            alert('error al guardar');
          },
        })
      );
    }
  }

  cancelar() {
    this.irAListado();
  }

  private irAListado() {
    this.router.navigate(['']);
  }

  get controlNombre() : FormControl{
    return this.formularioGroup.controls['nombre'] as FormControl
  }

  get controlApellido() : FormControl{
    return this.formularioGroup.controls['apellido'] as FormControl
  }

  get costoAproximado() : FormControl{
    return this.formularioGroup.controls['costoAproximado'] as FormControl
  }

  get controlContacto() : FormControl{
    return this.formularioGroup.controls['contacto'] as FormControl
  }

  get controlMarca() : FormControl{
    return this.formularioGroup.controls['marca'] as FormControl
  }

  get controlModelo() : FormControl{
    return this.formularioGroup.controls['modelo'] as FormControl
  }

}
