import { Component } from '@angular/core';
import { FormBuilder, FormControl, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { Subscription } from 'rxjs';
import { Rol } from 'src/app/Interfaces/rol';
import { Usuario } from 'src/app/Interfaces/usuario';
import { RolService } from 'src/app/Services/Usuario/Rol/rol.service';
import { UsuarioService } from 'src/app/Services/Usuario/Usuario/usuario.service';
import Swal from 'sweetalert2';

@Component({
  selector: 'app-user',
  templateUrl: './user.component.html',
  styleUrls: ['./user.component.css']
})
export class UserComponent {

  formularioGroup: FormGroup;
  usuario: Usuario = {} as Usuario;
  listaRoles: any = [];

  private subscription = new Subscription();
  roles: Rol[];

  constructor(

    private userService: UsuarioService,
    private rolService: RolService,
    private router: Router,
    private formBuilder: FormBuilder
  ) { 
    this.cargarRoles();
  }

  ngOnInit(): void {


    this.formularioGroup= this.formBuilder.group({
      nombre_apellido: ['', [Validators.minLength(3), Validators.maxLength(100), Validators.required]],
      usuario: ['', [Validators.minLength(3), Validators.maxLength(80), Validators.required]],
      password: ['', Validators.required],
      email: ['', [Validators.email,Validators.required]],
      id_rol: ['', Validators.required]
    })

    this.usuario = new Usuario();

    // this.subscription.add(
    //   this.rolService.getAllRoles().subscribe({
    //     next: (listado: Rol[]) => {
    //       this.roles = listado;
    //     },
    //     error: () => {
    //       alert('error al obtener roles');
    //     },
    //   })
    // );


  }
  async cargarRoles() {
    this.subscription.add(
      this.rolService.getAllRoles().subscribe({
        next: (respuesta: Rol[]) => {
          this.roles = respuesta;
        },
        error: () => {
          Swal.fire({
            title: 'Error al obtener el listado de roles',
            icon: 'error',
            confirmButtonText: "Ok",
          });
        },
      })
    );
  }

  guardar() {
    if (this.formularioGroup.valid) {
      this.subscription.add(
        this.userService.postCreate(this.usuario).subscribe({
          next: () => {
            this.irAHome();
          },
          error: () => {
            alert('error al guardar');
          },
        })
      );
    }
  }

  cancelar() {
    this.irAHome();
  }

  private irAHome() {
    this.router.navigate(['']);
  }

  get controlNombre() : FormControl{
    return this.formularioGroup.controls['nombre_apellido'] as FormControl
  }

  get controlUsuario() : FormControl{
    return this.formularioGroup.controls['usuario'] as FormControl
  }

  get controlPassword() : FormControl{
    return this.formularioGroup.controls['password'] as FormControl
  }

  get controlEmail() : FormControl{
    return this.formularioGroup.controls['email'] as FormControl
  }

  get controlRol() : FormControl{
    return this.formularioGroup.controls['id_rol'] as FormControl
  }



}
