    this.loginServ.authenticateUser(this.viewName);
    this.setUserLogged();
  }

  logOut() {
    Swal.fire({
      title: '¿Está seguro que quiere cerrar sesión?',
      icon: 'warning',
      showCancelButton: true,
      confirmButtonColor: '#3085d6',
      cancelButtonColor: '#d33',
      confirmButtonText: 'Si',
      cancelButtonText: 'No'
    }).then((result) => {
      if (result.value) {
        this.loginServ.setToken("", "", "");
        this.router.navigate(['login']);
      }
    })
  }

  setUserLogged() {
    this.loger.nombreUsuario= this.loginServ.getUserName();
    this.loger.rol = this.loginServ.getRol();
